  </section>
  <section class="page2 hide">
    <div class="logo"></div>
    <div class="vase-bg"></div>
    <p class="page2-title">进入本网站须年满<br/><strong>18</strong><span>岁</span></p>
    <div class="birthday">
      <p>请输入出生年月日</p>
      <div id="date">YYYY/MM/DD</div>
    </div>
    <div class="ok1 twinkle"></div>
    <div class="popup hide">
      <div class="content"><div class="close"></div></div>
    </div>
  </section>
  <section class="page3 hide">
    <div class="dragDom"></div>
    <div class="tips">
      <div class="handBox moveUp">
        <img src="https://touchworld.oss-cn-shanghai.aliyuncs.com/absolut/pad/point.png" class="point">
        <img src="https://touchworld.oss-cn-shanghai.aliyuncs.com/absolut/pad/hand.png" class="hand">
      </div>
    </div>
    <div class="next twinkle"></div>
    <div class="main">
      <div class="main-content">
        <div class="main-bg"><img src="images/60871222bb08f981b3f2967ae330897d.jpg" style="visibility: hidden"></div>
        <div class="main-vase"><img src="images/cae79a1c6996b4e342caab68ce9fd5ba.png" style="visibility: hidden"></div>
        <div class="main-el"></div>
        <div class="main-logo"><img src="images/8a00080e8a564308d7559c091481b89a.png"></div>
      </div>
    </div>
    <div class="select-wrap">
      <ul class="select-tab">
        <li class="tab1"></li>
        <li class="tab2"></li>
        <li class="tab3"></li>
      </ul>
      <ul class="select-content">
        <li class="tab tab1">
          <div class="content">
            <ul>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
            </ul>
          </div>
        </li>
        <li class="tab tab2">
          <div class="content">
            <ul>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
              <li></li>
            </ul>
          </div>
        </li>
        <li class="tab tab3">
          <div class="content">
            <ul>
              <li>
                <div class="el" data-index="1" data-imgName="21"></div>
                <div class="el" data-index="2" data-imgName="22"></div>
                <div class="el" data-index="3" data-imgName="23"></div>
                <div class="el" data-index="4" data-imgName="19"></div>
                <div class="el" data-index="5" data-imgName="20"></div>
                <div class="el" data-index="6" data-imgName="18"></div>
                <div class="category"></div>
              </li>
              <li>
                <div class="el" data-index="7" data-imgName="16"></div>
                <div class="el" data-index="8" data-imgName="17"></div>
                <div class="el" data-index="9" data-imgName="5"></div>
                <div class="el" data-index="10" data-imgName="4"></div>
                <div class="el" data-index="11" data-imgName="1"></div>
                <div class="category"></div>
              </li>
              <li>
                <div class="el" data-index="12" data-imgName="11"></div>
                <div class="el" data-index="13" data-imgName="3"></div>
                <div class="el" data-index="14" data-imgName="10"></div>
                <div class="el" data-index="15" data-imgName="13"></div>
                <div class="el" data-index="16" data-imgName="2"></div>
                <div class="category"></div>
              </li>
              <li>
                <div class="el" data-index="17" data-imgName="12"></div>
                <div class="el" data-index="18" data-imgName="15"></div>
                <div class="el" data-index="19" data-imgName="14"></div>
                <div class="el" data-index="20" data-imgName="19"></div>
                <div class="el" data-index="21" data-imgName="18"></div>
                <div class="category"></div>
              </li>
