                <div class="category"></div>
              </li>
              <li>
                <div class="el" data-index="27" data-imgName="26"></div>
                <div class="el" data-index="28" data-imgName="29"></div>
                <div class="el" data-index="29" data-imgName="27"></div>
                <div class="el" data-index="30" data-imgName="24"></div>
                <div class="el" data-index="31" data-imgName="25"></div>
                <div class="el" data-index="32" data-imgName="28"></div>
                <div class="category"></div>
              </li>
            </ul>
          </div>
        </li>
      </ul>
    </div>
  </section>
  <section class="page4 hide">
    <div class="return twinkle"></div>
    <div class="vase-bg"></div>
    <div class="image-box"></div>
    <div class="sign-box">
      <p>签名档</p>
      <input type="text">
      <div class="ok2 twinkle"></div>
      <div class="skip twinkle"></div>
    </div>
  </section>
  <section class="page5 hide">
    <div class="vase-bg"></div>
    <div class="tips"></div>
    <div class="image-box"></div>
    <div class="code">
      <p>输入你的打印验证码</p>
      <p><input type="text"></p>
    </div>
    <div class="btn">
      <div class="print twinkle"></div>
      <div class="again twinkle"></div>
    </div>
    <div class="qr_code"><img src="https://touchworld.oss-cn-shanghai.aliyuncs.com/absolut/pad/qr_code{{$id}}.png"></div>
    <div class="popup_qrCode">
      <div class="qrText">扫描二维码保存</div>
      <div class="qrCode"><img></div>
    </div>
  </section>
  <div class="popup-generate hide">
    <p>图片生成中...</p>
  </div>
  <div class="popup-print hide">
    <p>海报已递交打印<br/>请前往打印处查看</p>
  </div>
  <script>
    // 场次
    location_id = 10{{$id}}
    /*
     *  门店编号
     *  0：测试门店
     *  1：1好号门店
     *  2：2好号门店
     *  3：3好号门店
     *  4：4好号门店
     *  5：5好号门店
     */
    store = {{$id}}
  </script>
<script type="text/javascript" src="js/app.9c8df.js?9c8dfde2ad9b9b9435e9"></script></body>
</html>